INSERT INTO burger (burger, about, devoured) VALUES ('The Luther', "1lb ground beef covered with cheese grilled onions 5 strips of bacon between 2 Krispy Kreme doughnuts", false);
INSERT INTO burger (burger, about, devoured) VALUES ('The Big Kahuna', "1/2lb ground beef patty topped with avocado and a sweet and spicy pineapple habenero glaze that shows you whose boss served on a hawaiian rol", false);
INSERT INTO burger (burger, about, devoured) VALUES ('The Cheesy Western', "A thin beef patty topped with american cheese, a fried egg, yellow relish on a plain bun. Gross huh? come back after a night of drinking and have your life changed", false);
INSERT INTO burger (burger, about, devoured) VALUES ('The Black Mamba', "What else but 1/2lb Kobe sirloin cooked med or below with no toppings but a lil salt and pepper ", false);
INSERT INTO burger (burger, about, devoured) VALUES ('The Reaper', "On a Brioche bun, bacon jam,smoked mac+chz, ground sirlion, 3 strips candied pepper bacon, pepperjack + melted brie, porkbelly and maple mustard, Death never tasted so good", false);
INSERT INTO burger (burger, about, devoured) VALUES ('Prime Time', "1/2 prime rib oven finished with a jus, topped with shallot frites, 3 types smoked cheddar with, and a horsradish aoili", false);